import importlib
import json
mods = ["tests.services.test_event_schemas", "tests.controllers.test_paper_engine"]
results = []
all_ok = True
for m in mods:
    mod = importlib.import_module(m)
    fails = []
    total = 0
    for n in sorted([x for x in dir(mod) if x.startswith('test_')]):
        fn = getattr(mod, n)
        if not callable(fn):
            continue
        total += 1
        try:
            fn()
        except Exception as e:
            fails.append(f'{m}.{n}: {e}')
    ok = (len(fails) == 0 and total > 0)
    all_ok = all_ok and ok
    results.append({'module': m, 'total_tests': total, 'failures': fails, 'pass': ok})
print('__UNIT_JSON__' + json.dumps(results))
raise SystemExit(0 if all_ok else 1)
